<script>
    import { onMount } from 'svelte'

    let mask;
    let className;
    export { className as class };
    
    let container, intersecting;
    onMount(() => {
        const observer = new IntersectionObserver(entries => {
            intersecting = entries[0].isIntersecting;
            if (intersecting) {
                runWritingAnimation();
                observer.unobserve(container);
            }
        }, {
            threshold: .5
        });

        observer.observe(container);
    })

    function runWritingAnimation() {
        const paths = Array.from(mask.querySelectorAll('path'));
        let delay = 0;
        paths.forEach(path => delay = animateWriting(path, delay));
    }

    function animateWriting(path, delay) {
        setTimeout(() => {
            requestAnimationFrame(() => {
                path.classList.add('write');
            })
        }, delay);
        

        if (path.classList.contains('reg-stroke'))
            delay += 225;
        else if (path.classList.contains('short-stroke'))
            delay += 125;
        else
            delay += 350;

        return delay;
    }
</script>

